import React, { Component } from 'react';
import { Form } from 'react-bootstrap';

class Referral extends Component {
  constructor(props) {
    super(props);
    this.state = {
      titleInput: 'Swell Widgets Rule',
      layoutDropDown: 'single_row',
      additionalText: 'Swell Swell',
      submitText: 'Submit',
      socialShareOptions: ['facebook', 'twitter', 'sms', 'copylink', 'message']
    };

    this.handleInput = this.handleInput.bind(this);
    this.handleSocialCheckbox = this.handleSocialCheckbox.bind(this);
  }

  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      { [name]: value }
      //() => console.log(this.state)
    );
  }

  handleSocialCheckbox(e) {
    const newSelection = e.target.value;
    let newSelectionArray;

    if (this.state.socialShareOptions.indexOf(newSelection) > -1) {
      newSelectionArray = this.state.socialShareOptions.filter(
        s => s !== newSelection
      );
    } else {
      newSelectionArray = [...this.state.socialShareOptions, newSelection];
    }

    this.setState(
      prevState => ({
        socialShareOptions: newSelectionArray
      }),
      () => console.log(this.state)
    );
  }

  render() {
    return (
      <div className='referral-form'>
        <h1>Referral Widget Creator</h1>
        <Form>
          <Form.Group controlId='layout-dropdown'>
            <Form.Label>Email Input and Submit Button Layout</Form.Label>
            <Form.Control
              as='select'
              name='layoutDropDown'
              onChange={this.handleInput}
            >
              <option key='1' name='single-row' value='single_row'>
                1 Row Inline
              </option>
              <option key='2' name='double-row' value='double_row'>
                2 Rows
              </option>
            </Form.Control>
          </Form.Group>

          <Form.Group controlId='title-input'>
            <Form.Label>Form Title</Form.Label>
            <Form.Control
              as='textarea'
              rows='1'
              name='titleInput'
              value={this.state.titleInput}
              onChange={this.handleInput}
            />
          </Form.Group>
          <Form.Group controlId='additional-text'>
            <Form.Label>Form Additional Text</Form.Label>
            <Form.Control
              as='textarea'
              rows='3'
              placeholder='Swell rules. Everyone loves it. duh.'
              name='additionalText'
              onChange={this.handleInput}
            />
          </Form.Group>
          <Form.Group controlId='exampleForm.ControlTextarea3'>
            <Form.Label>Submit Button Text</Form.Label>
            <Form.Control
              as='textarea'
              name='submitText'
              rows='1'
              placeholder='Submit'
              onChange={this.handleInput}
            />
          </Form.Group>

          <Form.Group controlId='exampleForm.ControlTextarea5'>
            <Form.Label>Existing User Submit Button Text</Form.Label>
            <Form.Control as='textarea' rows='1' placeholder='Submit' />
          </Form.Group>

          <Form.Group controlId='exampleForm.ControlTextarea4'>
            <Form.Label>Social Share Links</Form.Label>
            <div key={`default-checkbox`} className='mb-3'>
              {['facebook', 'twitter', 'sms', 'copylink', 'message'].map(
                icon => (
                  <Form.Check
                    inline
                    type='checkbox'
                    id={`default-icon ${icon}`}
                    label={icon}
                    onChange={this.handleSocialCheckbox}
                    checked={this.state.socialShareOptions.indexOf(icon) > -1}
                  />
                )
              )}
            </div>
          </Form.Group>
        </Form>
        <hr />
        <p style={{ textAlign: 'center' }}>
          <b>Copy your referral widget code below:</b>
        </p>
        <pre>
          <code>
            {`
          <div
            class='swell-referral'
            swell-referral-form-layout='${this.state.layoutDropDown}'
            swell-referral-new-usertitle="${this.state.titleInput}"
            swell-referral-new-user-placeholder="${this.state.additionalText}"
            swell-referral-new-user-submit="${this.state.submitText}"
            swell-referral-share-links='${this.state.socialShareOptions}'
            swell-referral-exist-user-submit="Start"
            swell-referral-exist-user-placeholder=""
            swell-referral-exist-user-title=""
            swell-referral-shre-links-title=""
            swell-referral-popup-copy-link-text=""
            swell-referral-popup-thank-title=""
            swell-referral-popup-thank-text=""
            swell-referral-pre-header=""
            swell-referral-desc=""
          ></div>
        `}
          </code>
        </pre>
      </div>
    );
  }
}

export default Referral;
