import React, { Component } from 'react';
import { Form } from 'react-bootstrap';

class Referral extends Component {
  constructor(props) {
    super(props);
    this.state = { inputText: 'Swell Widgets Rule', layoutDropDown:'single_row' };

    this.handleInput = this.handleInput.bind(this);
  }

  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => {
        return {
          newUser: {
            ...prevState.newUser,
            [name]: value
          }
        };
      },
      () => console.log(this.state.newUser)
    );
  }

  render() {
    return (
      <div className='referral-form'>
        <h1>Referral Widget Creator</h1>
        <Form>
          <Form.Group controlId='exampleForm.ControlSelect1'>
            <Form.Label>Email Input and Submit Button Layout</Form.Label>
            <Form.Control
              as='select'
              name='layoutDropDown'
              onChange={this.handleInput}
            >
              <option key='1' name='single-row' value='single_row'>
                1 Row Inline
              </option>
              <option key='2' name='double-row' value='double_row'>
                2 Rows
              </option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId='title-input'>
            <Form.Label>Form Title</Form.Label>
            <Form.Control
              as='textarea'
              rows='1'
              name='inputText'
              value={this.state.inputText}
              onChange={e => {
                this.setState({ [e.target.name]: e.target.value });
              }}
            />
          </Form.Group>
          <Form.Group controlId='exampleForm.ControlTextarea2'>
            <Form.Label>Form Additional Text</Form.Label>
            <Form.Control
              as='textarea'
              rows='3'
              placeholder='Swell rules. Everyone loves it. duh.'
            />
          </Form.Group>
          <Form.Group controlId='exampleForm.ControlTextarea3'>
            <Form.Label>Submit Button Text</Form.Label>
            <Form.Control as='textarea' rows='1' placeholder='Submit' />
          </Form.Group>
          <Form.Group controlId='exampleForm.ControlTextarea5'>
            <Form.Label>Existing User Submit Button Text</Form.Label>
            <Form.Control as='textarea' rows='1' placeholder='Submit' />
          </Form.Group>
          <Form.Group controlId='exampleForm.ControlTextarea4'>
            <Form.Label>Social Share Links</Form.Label>
            <div key={`default-checkbox`} className='mb-3'>
              {['facebook', 'twitter', 'sms', 'copylink', 'message'].map(
                icon => (
                  <Form.Check
                    inline
                    type='checkbox'
                    id={`default-icon ${icon}`}
                    label={icon}
                  />
                )
              )}
            </div>
          </Form.Group>
        </Form>
        <hr />
        <p style={{ textAlign: 'center' }}>
          <b>Copy your referral widget code below:</b>
        </p>
        <pre>
          <code>
            {`
          <div
            className='swell-referral'
            data-formLayout='${this.state.layoutDropDown}'
            data-title='${this.state.inputText}'
            data-explain="Swell rules. Everyone loves it."
            data-new-user-submit="Go"
            shareLinks='facebook,twitter,sms,copylink,message'
            data-exist-user-submit="Start"
            data-exist-userplaceholder=""
          ></div>
        `}
          </code>
        </pre>
      </div>
    );
  }
}

export default Referral;
